css:
@-moz-document domain("pathoftranslation.com"), domain("disqus.com") {
* {
    font-weight: bold !important;
}

div.widgetizedArea {
    Background-color: rgba(0, 0, 0, 0.85) !important;
}

.novel_group .novel_entry {
    position: relative;
    margin-top: 12px;
    padding: 0;
    padding-bottom: 30px;
    padding-top: 18px;
    border-top: 1px solid #b61616;
    color: #189bea;
}

.widget, .page-header, .type-post, .type-page, .type-attachment, .post-pagination, .comments-area {
    margin-bottom: 1em;
    background-color: rgba(0, 0, 0, 0.85);
}

time.entry-date.published.updated {
    color: #b91616;
}

a.url.fn.n {
    color: #999 !important;
}

article[class^="post-"] p:nth-child(n){
    color: #999 !important;
}

article[class^="post-"] span.meta-comments > a{
    color: #189bea !important;
}
                                
article[class^="post-"] span.meta-category > a {
    color: goldenrod !important;
}

article[class^="post-"] h2 > a {
    color: #d53 !important;
}

aside[id^="wpstatistics_widget-"] li:nth-child(n) {
    color: #189bea !important;
}

aside[id^="meta-"] li:nth-child(n) > a {
    color: #999 !important;
}

form[id^="subscribe-blog-blog_subscription-"] > p:nth-child(n) {
    color: #189bea !important;
}

#disqus_thread {
    background-color: rgba(0, 0, 0, 0.85);
}

aside[id^="wppb-widget-"] > p:nth-child(n) {
    color: #189bea !important;
}

article[class^="post-"] h2 {
    color: red !important;
}

article[class^="post-"] h3:nth-child(n) {
    color: #0064ff !important;
}

a[class^="share-"] {
    background: #352a2a !important;
}

b, strong {
    font-weight: bold;
    color: #bd1f1f;
}
}
