css:
@-moz-document url-prefix("https://www.tumblr.com/") {
  
  /*                                                         MADE BY @PERSEPHONE on Stylish
	                                                      DO NOT STEAL THANKS!!! 
*/
  
  /** 
  * Instuctions:
  * - To change the text color, go to 'color' and change #fff to the color you want
  * 
  * - To change the #3D effect colors go to 'text-shadow' and change the #36e8c4 (blue) 
  * 	and #f506a3 (pink) to the colors you want
  * 
  * - To change the font go to https://fonts.google.com/ and find the font you would like to use and
  * 	1. Copy the link that is provided
  * 	2. Go to Code 2 and find '@font-face' then 'src' and change the url into the one that you copied
  * 	3. In '@font-face' and then 'font-family' and change 'Raleway' to the name of the font
  * 	4. Go back to Google Fonts and find 'Specify in CSS' and copy whatever is under it
  * 	5. In Code 1 and replace 'font-family: 'Raleway', sans-serif;' with what you coppied
  */


a, abbr, acronym, address, applet, article, aside, audio, b, big, blockquote, body, canvas, caption, center, cite, code, dd, del, details, dfn, div, dl, dt, em, embed, fieldset, figcaption, figure, footer, form, h1, h2, h3, h4, h5, h6, header, hgroup, html, i, iframe, img, ins, kbd, label, legend, li, mark, menu, nav, object, ol, output, p, pre, q, ruby, s, samp, section, small, span, strike, strong, sub, summary, div, sup, table, tbody, td, tfoot, th, thead, time, tr, tt, u, ul, var, video, hide_overflow, post_body {

	color: #fff!important;
    text-shadow: 1px 1px #36e8c4, 3px 1px #f506a3;
    font-family: 'Raleway', sans-serif;
}


}

@font-face {
    font-family: Raleway;
    src: url(https://fonts.googleapis.com/css?family=Raleway);
}
