css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("asciinema.org") {
  
  body, .main {
    background-color: #181818;
    color: #aaaaaa;
  }
  
  footer {
    background-color: #202020;
  }
  
  img {
    filter: invert(93%);
  }
  
  /***** Browse *****/
  
  /** 'Browse' options **/
  .nav > li > a:hover {
    background-color: #282828;
  }
  
  /** 'sort by' - date, popularity **/
  .btn-default {
    background-color: #282828;
    color: #aaaaaa;
  }
  
  .btn-default.active {
    background-color: #444444;
    color: #aaaaaa;
  }
  
  .btn-default:hover {
    background-color: #333333;
    color: #aaaaaa;
  }
  
  /** Video Item **/
  .preview .info {
    background-color: #282828;
    border-color: #202020;
  }
  
  .preview .info small {
    color: #aaaaaa;
  }
  
  /* 'Share', 'Download' - Overlay pop-up */
  .modal-content {
    background-color: #282828;
  }
  
  /* Settings - 'Edit', 'Make Public', 'Delete' */
  .dropdown-menu {
    background-color: #282828;
  }
  
  .dropdown-menu > li > a {
    color: #aaaaaa;
  }
  
  .asciicast-page section.meta .actions-dropdown button {
    background-color: #202020;
  }
  
  /** Bottom Page No.s **/
  .pagination > li > a, .pagination > li > span, .pagination > .disabled > a, .pagination > .disabled > a:hover {
    background-color: #282828;
  }
  
  .pagination > li > a:hover {
    background-color: #444444;
  }
  
  /***** /a/ - Video Page *****/
  section.odd {
    background-color: #202020;
  }
  
  /***** Docs *****/
  
  /* in-text code */
  code {
    background-color: #282828;
    color: #aaaaaa;
  }
  
  /* boxed code */
  body pre, pre {
    background-color: #282828;
    color: #aaaaaa;
  }
  
  /* keyboard key */
  kbd {
    background-color: #282828;
    color: #aaaaaa;
  }
  
  /***** Settings *****/
  
  .form-control {
    background-color: #282828;
    color: #aaaaaa;
  }

}
