css:
@-moz-document domain("twitter.com") {
body{font-family:tahoma,arial,verdana,sans-serif,Lucida Sans !important;}
/*Реклама и мусор*/
*[data-advertiser-id], .promoted,[data-promoted],.promoted-account,#empty-timeline-recommendations,[data-nav="ads"], .suggested-tweet-stream-container, .promoted.trend-name, .promoted-tweet, .trends.module.Trends, .flex-module, .WhoToFollow.is-visible, .PromptbirdPrompt--inline.PromptbirdPrompt, .SignupCallOut, .RelatedUsers {display: none !important;}
/*Галерея*/
.Gallery-media .media-image {width: 100% !important;height: 100% !important;}
/*Растянуто в ширину*/
#timeline, .ProfileHeading-toggle, .ProfileHeading-content {width: 800px;}
.stream, .stream-container, .permalink-tweet-container.permalink-inner, #permalink-overlay-body, #permalink-overlay-dialog, .PermalinkOverlay-content, .permalink-container--withArrows.permalink-container, .original-permalink-page.has-replies.stream-uncapped.light-inline-actions.permalink {width: 800px;}
.TweetArrows {width: 970px;}
.PermalinkOverlay-modal {margin-left: -300px;width: 800px;}
.ProfileHeading {width: 100%;}
/*Полная картинка*/
.is-square.AdaptiveMedia, .AdaptiveMedia-singlePhoto img {top:0px!important;bottom: 0px!important;border-radius:0px!important;max-height:100%!important;}
/*Квадратики*/
[class*='_img'],[class*='_image'],[class*='photo'],[class*='ava'], .thumb, img {border-radius: 0px !important; -moz-border-radius: 0px}
}
