css:
@-moz-document domain("rainymood.com") {
body {
  overflow-y: hidden !important;
}
.center-block {
  visibility: hidden !important;
}
.fb-like, .socials {
  display: none !important;
}
#volumeDiv {
  visibility: visible !important;
  opacity: 0 !important;
  position: absolute !important;
  top: 0 !important;
  left: 0 !important;
  padding: 10px !important;
}
#volumeDiv:hover {
  opacity: 0.5 !important;
}
body > div.center-container > a {
    display:none;
}
}
