css:
@-moz-document domain("userstyles.org") {
/** Creado por: Calista 17/9/16 **/

body, p {
    background: url(http://cdn.pcwallart.com/images/black-dragon-fire-wallpaper-1.jpg)no-repeat fixed top center!important; background-size: cover!important; color: White; font-family: Cambria, serif; font-size: 15px; font-weight: bold; text-shadow: 1px 2px 3px LightGray;
}

a:-webkit-any-link {
    color: PaleGoldenrod; text-decoration: none; text-shadow: 2px 3px 4px LightGray; transition-duration: 0.3s
}

a:-webkit-any-link:hover {
    color: LightGray
}

h1 {
    color: PaleGoldenrod; font-family: Cambria, serif; font-weight: bold; letter-spacing: 1px; text-transform: uppercase
}

h1:before, h1:after {
    content: '⚜'; margin-left: 5px; margin-right: 5px
}

header:after {
    content: url(http://www.tameraalexander.com/cmsimages/divider.gif); margin-top: 10px
}

#main-header {
    background: linear-gradient(to left, rgba(0, 0, 0, 0.8) 30%, rgba(139, 0, 0, 0.5) 50%, rgba(255, 69, 0, 0.5) 70%, rgba(255, 0, 0, 0.5) 100%); border: 4px groove PaleGoldenrod; border-radius: 15px; font-family: Cambria, serif; text-align: center
}

#left-sidebar>* {
    background: linear-gradient(to top, rgba(0, 0, 0, 0.5) 20%, rgba(139, 0, 0, 0.4) 35%, rgba(255, 69, 0, 0.4) 65%, rgba(255, 0, 0, 0.5) 100%); border: 3px groove PaleGoldenrod; border-radius: 10px; line-height: 20px; padding-bottom: 10px; padding-top: 10px
}

#left-sidebar a:hover:after {
    content: '⚡'; margin-left: 8px;
}

#search-terms {
    background: PaleGoldenrod; border: 3px groove DarkGoldenrod; border-radius: 4px
}

#search-submit {
    background: Sienna; border: 3px groove DarkGoldenrod; border-radius: 5px
}

#left-sidebar li, #left-sidebar li a {
    color: White; font-family: Cambria, serif;
}

.more {
    border-top: 2px solid PaleGoldenrod;
}

li, dd {
    font-family: Cambria, serif;
}

dt {
    color: Goldenrod; text-shadow: 1px 2px 3px DimGray;
}

#show-code pre {
    background: Black; border: 1px dashed DarkGray; border-radius: 5px; margin-top: 10px
}

tr {
    background: linear-gradient(to right, rgba(0, 0, 0, 0.8) 40%, rgba(139, 0, 0, 0.8) 65%, rgba(255, 69, 0, 0.8) 85%, rgba(255, 0, 0, 0.8) 100%);
}

input, table.form-controls, .form-controls>tbody>tr>td, textarea {
    background: PaleGoldenrod; border: 3px groove DarkGoldenrod; border-radius: 4px; font-family: Cambria, serif
}
}
