css:
@-moz-document domain("youtube.com") {
/*FADING*/

.resume-playback-background {
    opacity: 0.7 !important;
    height: 100% !important;
    width: 100% !important;
    background: #FFF !important;
}

.resume-playback-progress-bar {
    opacity: 0.5 !important;
}


/*STYLE*/
/*Search Bar*/
#masthead-search {
    margin: auto;
}

/*Buttons*/
.yt-uix-checkbox-on-off input[type=checkbox]:checked+label {
    background-color: #cc181e;
}

/*Links*/
.yt-uix-sessionlink.yt-ui-ellipsis.yt-ui-ellipsis-2.spf-link {
    color: #777 !important;
}

.g-hovercard.yt-uix-sessionlink.spf-link {
     color: #777 !important;
}

.yt-uix-expander-head {
     color: #777!important;
}

/*Backgrounds*/
#body {
    background: #FFF !important;
}

#page {
    background: #FFF !important;
}

#masthead-positioner-height-offset {
    background: #FFF !important;
}

#yt-masthead-container {
    border-bottom: 0px solid #FFF !important;
}

#masthead-appbar {
    border-bottom: 0px solid #FFF !important;
}

.search-header {
    border-bottom: 0px solid #FFF !important;
}

.yt-card {
    box-shadow: none !important;
}

/*Only when hovering*/
.yt-lockup-meta {
    color: #FFF !important;
}

.yt-lockup-meta:hover {
    color: #777 !important;
}

.yt-lockup-description {
    color: #FFF !important;
}

.yt-lockup-description:hover {
    color: #777 !important;
}

.yt-uix-redirect-link {
    color: #FFF !important;
}

.yt-uix-redirect-link:hover {
    color: #777 !important;
}

/*Removing*/
#logo-container {
    display: none !important;
}

.yt-lockup-badges {
    display: none !important;
}
}
