css:
@-moz-document domain(starladder.com) {
/*@import 'https://fonts.googleapis.com/css?family=Tinos&subset=cyrillic';*/
body {
    background: #ffffff;
}

body,
.events .card .event-name,
.events .card .discipline,
.btn-sm.event-status,
.news .top-news-item .meta .excerpt,
.hp-items-list--item .cat-name,
.news-item-single-col .entry-title,
.news-item-single-col .entry-content
{
    font-family: 'Tinos', serif;
}

#header {
    position: fixed;
    width: 100%;
    z-index: 1;
    top: 0;
}

#content {
    margin-top: 85px;
}
.events .card {
transition: border .5s;
}
.events .card:hover {
    border: .0625rem solid #c3c2c2;
}

#header .navbar-nav a {
    font-size: 16px;
}

.card article.card-block.hp-items-list--item:hover {

	transition: box-shadow .5s;
}
.card article.card-block.hp-items-list--item:hover {
    box-shadow: 1px 1px 1px 1px #ccc;
}

.card .hp-items-list--item .cat-name {
    position: absolute;
    top: 0;
    background: rgba(255, 255, 255, 0);
    left: 0;
    color: #212121;
    padding: 5px 10px;
    text-transform: uppercase;
    border-bottom-right-radius: 5px;
}

.card .hp-items-list--item .cat-name a{
    color: #fff;
}

.news .card-block {
    position: relative;
}

.card .hp-items-list--item .post-date {

    position: absolute;
    text-align: right;
    top: 0;
    right: 0;
    padding: 10px;
    color: #fff;
        text-shadow: 1px 1px 1px #000;
}

.card .hp-items-list--item .news-excerpt {
    position: relative;
    top: -75px;
}

.card .hp-items-list--item .entry-title a {
    position: absolute;
    top: 140px;
    left: 0;
    padding: 10px 20px;
    color: #fff;
    text-shadow: 1px 1px 1px #000;
    background: rgba(0, 0, 0, 0.42);
}

.card .hp-items-list--item .entry-title a:hover {
color: #fff;
}

.card .archive .hp-items-list--item .card-text {
    height: 90px;
}
.match-info-layout
{
	flex-direction: column;
}

.match-info-content
{
    margin-right: 0;
    padding-right: 0;
}
.match-info-aside
{
    width: 100%;
}

.team-composition-person .person-img-holder {
    width: 154px;
    height: 154px;
}
.team-composition-person .person-box {
    padding-right: 147px;
    min-height: 155px;
    display: flex;
    flex-direction: column;
    justify-content: center;
}

.team-composition-person.mod-reverse .person-box {
    padding-left: 157px;
}

.team-composition-person {
    min-height: 144px;
}
.info-widget .widget-title {
    display: block;
    width: 100%;
    text-align: Center;
}

}
