css:
@-moz-document domain("modthesims.info") {
/*===========COLOR PALETTE===========*/

:root {
    --primary: #20364F;
    --accentlight: #b8c9e0;
    --accentdark: #152639;
    --heavy: #0C1824;
    --menutile: #FFFFFF;
    --menusection: #d1d1d1;
}
/*===================================*/
/*BODY*/
body {
    font-family: 'Open Sans', 'Tahoma', sans-serif;
    color:
    /*b.ac*/
    
    gray;
    background:
    /*b.bg*/
    
    lightgray;
}
* {border-radius:0px;}
/*MAIN CONTENT SPAN*/

/*MENU BAR*/
div#menubar {
    background-color: var(--primary);
    border: none;
}
#cbp-hrmenu > ul > li.fake-pills.cbp-hropen > div {
    border-radius: 0px;
}
.cbp-hrmenu .cbp-hrsub-inner > div a {
    color: var(--menutile);
}
.cbp-hrsub.well {
    background-color: var(--primary);
}
input.bginput.input-small {
    border-radius: 0px;
}

/*TEXT*/
.headingtitle {
    font-variant: normal;
    font-weight: bold;
    font-size: 2.0em;
    padding-bottom: 0.5em;
}

/*ALERT*/
.alert,
.alert-success {
    border-radius: 0px;
    background-color: #20364F;
    border-color: darkgray;
    color: white;
}
.alert h4,
.alert-success h4 {
    color: white;
}
.alert a,
.alert-success a {
    color: #b8c9e0;
    font-weight: bold;
}
#myCarousel > div > div > div {color:var(--accentdark);}
#myCarousel > div > div > div a {color:var(--accentdark);}

/*MISC*/
.btn-success {
    background-color:var(--accentdark);
    border:none;
    border-radius:0px;
}
.btn-success:hover {
    background-color:var(--accentlight);
    border:none;
    border-radius:0px;
}
.btn-primary {
    background-color:var(--accentdark);
    border:none;
    border-radius:0px;
}
.btn-primary:hover {
    background-color:var(--accentlight);
    border:none;
    border-radius:0px;
    color:white;
}

/*FOOTER*/
#menubar > div > div:nth-child(2) {line-height: 14px; font-size:12px; margin:none;}
}
