css:
@-moz-document url-prefix("http://oll.tv") {
/*Спрятал кнопку*/
.widget-button {display: none !important;}
.back-call {display: none !important;}
.title-toolbar {display: none !important;}

/*Фон*/
.header-inner {
    background: black;
    color: #777777;   
}

/*Верхнее меню*/
.top-menu li a {
    background: black;
    color: #777777; 
}

/*Поиск*/
.form-search-header .search-query {
    background: black;
    color: #777777; 
}

/*Фон верхнее меню*/
.header-bottom {
    background: black;
    color: #777777;
}

/*Фон над меню*/
.header-top {
    background: black;
    color: #777777; 
}

/*Меню*/
.header-bottom .container-inner {
    background: black;
    color: white;
}

/*Меню кнопки*/
.nav-header .item1-inner a {
    color: #ececec;
}

/*Шапка*/
.header-top .container-inner {
    background: black;
    color: #777777;
}
body {
    color: #333333;
    background: black;
}

/*Лента матчей*/
.section-wrapper-inner {
    background: black;
    color: #777777;
}

/*Прямые трансляции*/
.content-header h1 {
    color: #777777;
}

.section-wrapper-inner h2 {
    color: #777777;
}

.section-wrapper h1 {
    color: #777777;
}

/*Цвет шрифта заголовка матча*/
.section-wrapper h1 {
    color: #777777;
}

/*Черный фон "Смотри прямо сейчас*/
.btn-blue {
    background:#000000;
}

/*Черный фон таблички*/
.nav-filter {
    background: #000000;
    border: 1px solid #6b6a6a;
}

/*Белый шрифт таблички*/
.live-list a {
    position: relative;
    color: #777777;
}

/*Серый шрифт главного меню*/
.nav-header .item1-inner a {
color: #777777;
}

}
