css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("live24.gr") {
  body{
    background-color: #000 !important;
    color: #eee !important;
  }
  #page, #content, .googlesearch, #footer{
    background-color: #224 !important;
  }
  #stationinfo > h2 {
    color: #bbb !important;
  }
  #stationinfo > a {
    color: #aa0 !important;
  }
  #menu, #menu > h3{
    background: #111 !important;
  }
 
  #areas, #submenu{
    background: #335 !important;
  }
  #areas a, #submenu a, .name, .links a{
    color: #ccc !important;
  }
  .banner, #recommendation{
    display: none !important;
  }
  .gsc-control-cse, .gsc-results, .gsc-webResult, .gs-title, .gs-visibleUrl , .gsc-table-cell-snippet-close, .gs-snippet, .gsib_b, #gsc-iw-id1 {
    background-color: #222 !important;
    color: #eee !important;
  }
  input, textarea, #gs_tti50{
    background: #224 !important;
    color: #eee !important;
  }
  #contact input, textarea, #gs_tti50{
    border: 2px solid #285896 !important;
  }
  #l24logo, .logo{
    filter: invert(100%) hue-rotate(180deg) !important;
  }
  #footer{
    background-image: none !important;
    color: #eee !important;
	  border-top: 20px solid #113 !important;
  }
  label{
    color: #ddd;
  }
  #poweredby{
    background-color: #ada1b1;
  }
  #leftRadioList, #submenu, #rightRadioList{
        background-image: none;
  }
}
