css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("game20.gr") {
  
  article, section, .post-title, body{
    background: #262630 !important;
    color: #eee !important;
  }
  a{
    color: #ddd !important;
  }
  .author > a{
    color: #98c !important;
  }
p > a{
    color: #66f !important;
  }
  .date-author > a{
    color: #f11 !important;
  }
  .header{
    filter: invert(100%) hue-rotate(180deg) saturate(360%) brightness(0.3) !important;
  }
  .sub > li > a > span{
    color: #000;
  }
  .logo a{
    height: 103px !important;
  }
  .content > p, .yarpp-thumbnail-title, .search > div > h2, .newsletter > p{
    color: #eee !important;
  }
  .comments-info, #disqus_thread{
     background: #222229 !important;
     color: #eee !important;
  }
  h2{
   background:  #202023 !important;
  }
  .page-numbers, .page-numbers a{
     background:  #575260 !important;
  }
}
