css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("r.sf-misis.ru") {
  ::-webkit-input-placeholder {
    color:white;
  }
  ::-moz-placeholder {
    color:white;
  }
  :-moz-placeholder {
    color:white;
  }
  :-ms-input-placeholder {
    color:white;
  }
  html, body {
    background-color: #6DBCE3;
  }
  td {
    background-color: #42A1D1;
  }
  #search-tools, .search-select, .search-text {
    background-color: #6DBCE3;
    color: white;
    border-color: #2397D1;
  }
  #schedule-table th {
    background-color: #2397D1;
    font-size: large;
    font-weight: 900;
    color: white;
    /*width: calc(100%/8);*/
  }
  #title {
    background-color: #2397D1;
    font-weight: bold;
  }
  #title a{
    color: white;
  }
  .table-single:hover {
    background-color: #D8D8F0;
  }
  .table-subgroups {
    background-color: #e5ffe5;
  }
  .table-subgroups:hover {
    background-color: #D3F4D3;
  }
  .table-subgroup-item {
    margin-bottom: 0;
  }
  .table-subgroups:hover .table-subgroup-item {
    background-color: #D3F4D3;
  }
  .table-empty {
    background-color: #42A1D1;
  }
  .table-subject {
    font-weight: bold;
  }
  .misc {
    background-color: #6DBCE3;
    color: white;
  }
  #footer-feedback {
    color: white;
  }
  .table-room {
    font-size: large;
    font-weight: 900;
  }
  .result-item {
    background-color: #42A1D1;
  }
  .result-item-header {
    border-bottom: 1px solid #187DB0;
    color: white;
  }
  .result-item  div, a {
    color: white;
  }
  #schedule-table td, #schedule-table th {
    border: none;
  }
  .table-sg-name {
    margin: 0;
  }
  .session-item {
    background-color: #42A1D1;
  }
  .session-item  div {
    color: white;
  }
}
