css:
@-moz-document domain(logicgamesonline.com) {
body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
    color: #ddd;
    background-color: #222 !important;
}
#wrapper {
    background-color: #333 !important;
}
#header {
    background-color: #222 !important;
}
ul.toplevel li {
    background-color: #111 !important;
    border: 1px solid #222;
}
#explain {
    border: 1px dashed #666;
}
#footer {
    border-top: 2px solid #666
}
ul.toplevel li li {
    background-color: #333 !important;
}
#contain table tbody tr td {
    background-color: #222;
    color: #000;
}
#movetable {
    background-color: #222 !important;
}
.toolbox tbody tr td {
    background-color: #333 !important;
}
.toolbox tbody tr:first-child td {
    background-color: #111 !important;
}
.bottombox tbody tr td {
    background-color: #333 !important;
}
.bottombox tbody tr td:first-child {
    background-color: #111 !important;
}
table.bottombox tr td {
    border-color: #111 !important;
}
table.toolbox tr td {
    border-color: #111 !important;
}
a {
    color: #608dff !important;
    text-decoration: underline;
}
a:hover {
    color: #637ab5 !important;
    text-decoration: underline;
}
.year a {
    text-decoration: None;
    display: block;
 	width: 100%;   
}
.year a:hover {
    text-decoration: None;
    display: block;
 	width: 100%;
    background-color: #333;
}
.year a:visited {
    color: #ee0000 !important;
    text-decoration: underline;
}
table.year tr td{
           border-color: #333;
}
table.year tr td table tbody tr td {
 	background-color: #444;
}
table.year tr td table tbody tr:first-child th {
    border-color: #333;
    background-color: #333;
}
table.year > tbody > tr > th {
    border-color: #444;
    border: 0px;
    background-color: #333;
}
select {
    background-color: #777;
    color: #ddd;
    border-color: #222;
}
input {
    background-color: #333;
    color: #ddd;
    border-color: #444;
}
h2 {
    background-color: #333;
}
h3 {
    background-color: #222;
}
body > h3 {
    background-color: #333;
}
tr.menu td {
    background-color: #111;
}
}
