/* i really want this to be global */
.ytp-play-progress {
	background: linear-gradient(left,#FF0000,#0000FF,#00FF00,#FF00FF,#00FFFF,#FFFF00,#000000, #FF00FF, #FFFFFF);
	background: -moz-linear-gradient(left,#FF0000,#0000FF,#00FF00,#FF00FF,#00FFFF,#FFFF00,#000000, #FF00FF, #FFFFFF);
	background: -o-linear-gradient(left,#FF0000,#0000FF,#00FF00,#FF00FF,#00FFFF,#FFFF00,#000000, #FF00FF, #FFFFFF);
	background: -webkit-linear-gradient(left,#FF0000,#0000FF,#00FF00,#FF00FF,#00FFFF,#FFFF00,#000000, #FF00FF, #FFFFFF);
}
.ytp-hover-progress-light {
 	background: linear-gradient(left,#FF8080,#8080FF,#80FF80,#FF80FF,#80FFFF,#FFFF80,#808080, #FF80FF, #F8F8F8);
	background: -moz-linear-gradient(left,#FF8080,#8080FF,#80FF80,#FF80FF,#80FFFF,#FFFF80,#808080, #FF80FF, #F8F8F8);
	background: -o-linear-gradient(left,#FF8080,#8080FF,#80FF80,#FF80FF,#80FFFF,#FFFF80,#808080, #FF80FF, #F8F8F8);
	background: -webkit-linear-gradient(left,#FF8080,#8080FF,#80FF80,#FF80FF,#80FFFF,#FFFF80,#808080, #FF80FF, #F8F8F8);
}
.ytp-unloaded-progress {
	background: linear-gradient(left ,#191919, #4d4d4d, #4C0000, #4c3200, #4c4c00, #002600, #00004c, #260026, #36264c, #4d4d4d, #191919);
  	background: -moz-linear-gradient(left ,#191919, #4d4d4d, #4C0000, #4c3200, #4c4c00, #002600, #00004c, #260026, #36264c, #4d4d4d, #191919);
  	background: -o-linear-gradient(left ,#191919, #4d4d4d, #4C0000, #4c3200, #4c4c00, #002600, #00004c, #260026, #36264c, #4d4d4d, #191919);
  	background: -webkit-linear-gradient(left ,#191919, #4d4d4d, #4C0000, #4c3200, #4c4c00, #002600, #00004c, #260026, #36264c, #4d4d4d, #191919);
}
.ytp-load-progress {
  background: rgba(90, 90, 90, .5);
}
