css:
@-moz-document regexp(".*lichess\\.org/tournament.*") {
button.fbt.text {
    border-radius: 0 !important;
    border: 1px solid rgba(100, 100, 100, 250) !important;
    color: rgba(255, 255, 255, 255) !important;
    top: 0 !important;
    height: 100% !important;
    right: 0 !important;
    background-color: rgba(215, 113, 11, 0) !important;
    transition: 0s none !important;
}
button.fbt.text:hover {
    background-color: rgba(56, 147, 232, 255) !important;
}
button.fbt.text.highlight:active {
    background-color: #333 !important;
    margin-top: -8px !important;
    height: 41px !important;
}
button.fbt.text.highlight:hover {
	background-color: rgba(215, 113, 11, 255) !important;
}
button.fbt.text.disabled {
    color: rgba(116, 116, 116, 255) !important;
}
button.fbt.text.disabled:hover {
    background-color: rgba(215, 113, 11, 0) !important;
}
}
