css:
@-moz-document domain("tomato-timer.com") {
body {
    background-color: #1e1e1e;
}
h1,
h5,
h6.subheader,
a#timer_start,
a#timer_pause,
h2,
h3 {
    color: #cccccc;
}
.panel {
    background-color: #282828;
    color: #cccccc;
    border-color: black
}
button#timer_reset,
input {
    background-color: #282828;
    color: #cccccc;
    border-color: gray;
}
a#pomodoro.button,
a#short_break.button,
a#long_break.button,
a.small.button {
    background-color: #292929;
    color: #cccccc;
    border-color: gray;
    box-shadow: 0 0px 0;
}
.reveal-modal,
option {
    background-color: #282828;
    color: #cccccc;
}
}
