css:
@-moz-document url-prefix("https://www.evernote.com/Home.action#") {
/* Reposition Lazza's Link */
div[style*="position: fixed"][style*="left: 4rem"] {
  right: 4.5rem;
  left: initial !important;
  bottom: 0.5rem !important;
  font-size: 11px;
}
/* ensmallen the editor text */
div#html_code_editor > #html_code_area {
  font-size: 11px !important;
}
}
