css:
@-moz-document domain(play.google.com) {
@media only screen and (min-width: 1300px) {
    #music-content .g-content {
        margin: 16px 2px 30px 72px !important;
    }
    #drawer {
        right: 0 !important;
        position: fixed;
        top: 0;
        transition: initial !important;
        visibility: visible !important;
        transform: initial !important;
    }
    #main {
        right: 0 !important;
        left: 220px !important;
    }
    #queue-overlay {
        right: 320px !important;
    }
    #player, #music-content {
        left: 60px !important;
        padding-right: 320px;
    }
    #player .player-progress-wrapper,
    #material-player-right-wrapper {
        right: 60px !important;
    }
}
}
