css:
@-moz-document domain('mlpach.ru') {
    div.post.reply {
        background-color: #282a2e !important;
        border: 2px solid #282a2e;
        border-radius: 1px !important;
        box-shadow: 1px 1px 5px 1px rgba(0,0,0,0.35);
    }
    .name {
        color: #C5C8C6 !important;
    }
    html {
        background-color: #1D1F21;
        color: #C5C8C6 !important;
    }
    body {
        background-color: #1D1F21;
        color: #C5C8C6;
    }
    a, .a-link-emulator {
        color: #81a2be !important;
    }
    hr {
        border-top: 1px solid rgba(221,221,221,0.05) !important;
    }
    a:hover, .a-link-emulator:hover {
        color: #5F89AC !important;
    }
    .pages {
        background-color: #1D1F21 !important;
        border-bottom: 0px !important;
    }
    .subject {
       color: #C5C8C6 !important;  
    }
    header div.subtitle {
        color: #C5C8C6 !important;  
    }
    .highlighted {
        border: 2px dashed #C5C8C6 !important;
    }
    .banner {
        background-color: #282a2e !important;
        border: 1px solid #282a2e !important;
        border-radius: 1px !important;
        box-shadow: 1px 1px 5px 1px rgba(0,0,0,0.35) ;
        color: #C5C8C6 !important;  
    }
    .post-hover {
        box-shadow: rgba(0,0,0,0.75) 1px 1px 1px !important;
        border: 1px solid #1D1F21 !important;
    }
}
