css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.one.com") {

  .one-bar {
    background: #808080;
    color: #fff;
}
.one-bar--applications--item.active {
    background-color: #eb1b75;
}
.one-bar--applications--item:hover {
    background-color: #eb1b75;
}
.one-bar--logo {
    display: none;
}
.one-button-style--primary {
    background-color: #eb1b75;
}
.slide-in .slide-in-container .slide-in-toggle {
    background-color: #eb1b75;
}
.special-offer-button-sanebox {
    display: none;
}
.special-offer-button-eas {
    display: none;
}
}
