css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.skai.gr") {
  
  body, #main-holder, .footer-holder, #column-main, #column-sec, .category-featured, .category-block, .newsroom-container, .divider-featured, .title, #watch .contents, .latest, #column-main header, .nav-container, .vertical-tiles article, .nav-menu-main, .skaikairos, .articles-side-box, #traffic, #traffic .contents, #traffic ul.tabs li.active, #watch ul.tabs li.active, #watch ul.tabs li  {
    background-color: black;
    background: black;
    color: white;
  }
  
  .left-col p, .right-col h2 a, .left-col h2 a, .category-block h1 a, .article-title, .article-subtitle {
    color: white;
  }
  
  .triggers ul, .triggers ul li h5 {
    background-color: black;
    background: black;
    color: white;
  }
  
  .middle-featured, .sticky{
    background: none;
  }

}
