css:
@-moz-document domain("gaiaonline.com") {
#gaia_content #guild_header {
	background: lightblue;
	padding-left: 15px;
	padding-top: 0px;
	padding-bottom: 1px;
	min-height: 68px;
	clear: both;
}
#guild_homepage {
	margin: 0px;
	position: relative;
	clear: both;
}
#guild_homepage .guild_right_column {
	position: relative;
	width: 300px;
	float: right;
}
#guild_homepage .guild_right_column .guild_module {
	border: 1px solid lightblue;
	font: 85% arial, helvetica, clean, sans-serif;
	background: lightblue;
	margin-bottom: 5px;
	margin-right: 5px;
	padding-bottom: 5px;
	padding-left: 5px;
	padding-right: 5px;
}
#guild_homepage .guild_right_column .guild_module h3 {
	background: repeat-x scroll 0 0 grey;
	font-size: 12px;
	padding: 3px;
	color: black;
	margin-top: 2px
}
#content .gaia_navigation_tabs .yuimenu li a,
#content .gaia_navigation_tabs .yuimenu li a:hover {
	display: block;
	font: 85% arial, helvetica, clean, sans-serif;
	background-color: lightblue;
	background-image: none;
	color: lightblue;
	float: none;
	text-align: left;
	margin: 0;
	padding: 4px;
	font-weight: normal;
	text-indent: 0;
	height: 12px;
	line-height: 12px;
	min-width: 100px;
	width: auto;
	_width: 100px;
	border: 0;
}
}
