css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(archive.sap.com) {

  .breadcrumb .container{
    height: 35px;
    padding: 5px;
    max-height: 35px;
    min-height: 35px; 
  }
  
  .container .content-wrapper {
    padding-top: 5px;
    padding-bottom: 5px;
  }

  h1.app-heading {
    font-size: 26px;
    line-height: 1.4;
    margin-top: 10px;
    margin-bottom: 1px;
  }
  
  .block-answered-info, .da-wrapper-body{
    margin-top: 0px;
  }
  
  .col-md-16 {
    width: 100%;
  }
  
  .author-block {
    display: none;
  }
  
  .question-text {
    padding-top: 10px;
  }
  
  .row {
    margin-bottom: 5px;
  }
  
  .da-jumbotron {
    padding-top: 1px;
  }
  
  .da-jumbotron > .content-wrapper {
    padding-top: 1px;
  }
  
  .thread-replies.meta-data {
    margin-top: 5px;
    margin-bottom: 5px;
    font-size: 14px;
  }
  .question-text p {
    line-height: 1.4;
  }
  
  .break-line-internal {
    padding-top: 5px;
  }
  
  .label-like {
    display: none;
  }
  
  .hidden-like-messag {
    display: none;
  }
  
  .hidden-like-message {
    display: none;
  }
   
  .answerMessage {
    margin-top: -15px;
  }
  
}
