css:
@-moz-document domain(viart.com) {

#m_49 {display: none !important;}
#img49 {display: none !important;}
#m_50 {display: none !important;}
#img50 {display: none !important;}
#m_52 {display: none !important;}
#img52 {display: none !important;}
#m_53 {display: none !important;}
#img29 {display: none !important;}

.ni-testimonials {display: none !important;}
.ni-blocks {display: none !important;}
.php-shopping-cart {display: none !important;}

.block-forum-latest .padding10px td {display: block !important; width: 99%;}
.block-forum-latest span {color: #666666 !important; display: block !important; font: 9px/24px "Trebuchet MS",Arial,sans-serif; float: left; width: 110px;}
.block-forum-latest div {display: inline !important;}
.block-forum-latest div a {font: 18px/24px "Trebuchet MS",Arial,sans-serif; text-decoration: none;}
.block-forum-latest div a b {font-weight: normal;}

td.medal {background-image: url(http://www.viart.com/images/c2s/ni-detki.jpg) !important;}
table.ni-promo-in .medal div {background: none !important;}
table.ni-promo-in .medal div a {display: none !important;}

.topCategory {background: none !important; margin: 0px; padding: 0px !important;}
.selectedtopCategory {background: none !important; border: none !important; margin: 0px; padding: 0px !important;}
.subCategory {background: none !important; margin: 0px; padding: 0px !important;}
.selectedsubCategory {background: none !important; border: none !important; margin: 0px; padding: 0px !important; }
.topCategory img, .subCategory img, .selectedtopCategory img, .selectedsubCategory img {display: none;}
.topCategory a.title, .selectedtopCategory a.title {display: block; padding: 4px 0px 4px 15px;}
.subCategory a.title, .selectedsubCategory a.title {display: block; padding: 2px 0px 2px 30px;}
.topCategory a.title {background: url(http://www.viart.com/images/c2s/arrow-lmenu.gif) no-repeat 0px 6px;}
.selectedtopCategory a.title {background: url(http://www.viart.com/images/c2s/arrow-yellow.gif) no-repeat 0px 6px;}
.subCategory a.title {background: url(http://www.viart.com/images/c2s/arrow-brown.gif) no-repeat 18px 6px;}
.selectedsubCategory a.title {background: url(http://www.viart.com/images/c2s/arrow-yellow.gif) no-repeat 16px 4px;}

}
