@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("voscomptesenligne.labanquepostale.fr") {

	/* supprime l’image d’édito */
	
	.CQblocedito {
		display: none;
	}
	
	/* redonne des couleurs aux valeurs */
	
	#main .num span.negatifTxt {
		color: #dc322f !important;
	}
	
	#main .num span.positifTxt {
		color: #859900 !important;
	}
	
	/* fait grandir les valeurs */
	
	#main .num span {
		font-size: 1.1em !important;
	}	
	
}
