css:
@-moz-document domain("put.io") {
#content {
background-color: #000000 !important;
}

#app {
background: #000 !important;
}

body, html {
background-color: #000000 !important;
}

header {
background: #fdce45 !important;
}

aside {
background-color: #424242 !important;
border-right: 2px solid #fdce45 !important;
}

body, import {
text-shadow: 0px 0px 10px #000000 !important;
color: #FFFFFF !important;
}

aside footer a {
background: black !important;
border-right: 2px solid #fdce45;
}

aside footer {
border-top: 2px solid #fdce45 !important;
}

.friend_user_item .passive {
background: rgba(0,0,0,0);
}

.transfer-header {
background-color: #424242 !important;
border-bottom: 2px solid #fdce45 !important;
}

.file-list-header {
background-color: #424242 !important;
border-bottom: 2px solid #fdce45 !important;
}

.checkbox label {
background-color: rgba(0,0,0,0) !important;
}

.file-list-item:hover {
background-color: #424242 !important;
}
}
