/* i really want this to be global */
.ytp-play-progress {
	background: linear-gradient(left,#FFFF00,#00FFFF,#000000,#FFFFFF,#0000FF,#FF0000,#00FF00, #FFFFFF, #FF00FF);
	background: -moz-linear-gradient(left,#FFFF00,#00FFFF,#000000,#FFFFFF,#0000FF,#FF0000,#00FF00, #FFFFFF, #FF00FF);
	background: -o-linear-gradient(left,#FFFF00,#00FFFF,#000000,#FFFFFF,#0000FF,#FF0000,#00FF00, #FFFFFF, #FF00FF);
	background: -webkit-linear-gradient(left,#FFFF00,#00FFFF,#000000,#FFFFFF,#0000FF,#FF0000,#00FF00, #FFFFFF, #FF00FF);
}
.ytp-hover-progress-light {
 	background: linear-gradient(left,#FFFF80,#80FFFF,#808080,#F8F8F8,#8080FF,#FF8080,#80FF80, #F8F8F8, #FF80FF);
	background: -moz-linear-gradient(left,#FFFF80,#80FFFF,#808080,#F8F8F8,#8080FF,#FF8080,#80FF80, #F8F8F8, #FF80FF);
	background: -o-linear-gradient(left,#FFFF80,#80FFFF,#808080,#F8F8F8,#8080FF,#FF8080,#80FF80, #F8F8F8, #FF80FF);
	background: -webkit-linear-gradient(left,#FFFF80,#80FFFF,#808080,#F8F8F8,#8080FF,#FF8080,#80FF80, #F8F8F8, #FF80FF);
}
.ytp-unloaded-progress {
	background: linear-gradient(left ,#191919, #4d4d4d, #4C0000, #4c3200, #4c4c00, #002600, #00004c, #260026, #36264c, #4d4d4d, #191919);
  	background: -moz-linear-gradient(left ,#191919, #4d4d4d, #4C0000, #4c3200, #4c4c00, #002600, #00004c, #260026, #36264c, #4d4d4d, #191919);
  	background: -o-linear-gradient(left ,#191919, #4d4d4d, #4C0000, #4c3200, #4c4c00, #002600, #00004c, #260026, #36264c, #4d4d4d, #191919);
  	background: -webkit-linear-gradient(left ,#191919, #4d4d4d, #4C0000, #4c3200, #4c4c00, #002600, #00004c, #260026, #36264c, #4d4d4d, #191919);
}
.ytp-load-progress {
  background: rgba(90, 90, 90, .5);
}
