.c-panel_content,
.c-dropdown__select ul,
#_popIn_img3_pc ._popIn_recommend_art_img {
	border-radius: 5px;
	box-shadow: 0 1px 1.5mm #888
}

.c-authorCard,
.l-wrap>.u-gapNextV--lg:nth-of-type(2),
.l-content__side .c-articleCard,
.c-group__options,
.c-dropdown--sort .c-dropdown__select ul,
.l-listTable,
.l-articlePage,
.l-searchCard,
.l-memberIntro,
.l-separationBg,
.l-wrap>div[itemprop="articleBody"] {
	border-radius: 2mm;
	box-shadow: 0 1px 2mm #666
}

.l-signInPage__content,
.l-content__main {
	border-radius: 3mm;
	box-shadow: 1px 2px 2mm #444
}

@media screen and (min-width: 0) {
	.c-menuLv1 a {
		font-size: 12pt;
		padding: 6px .25em
	}

	.l-footerLinks>li a {
		font-size: 10pt
	}

	.t1,
	.t2 {
		font-size: 19px !important
	}

	.o-mainCardDesc {
		font-size: 16px
	}

	.c-mainCard__desc {
		padding: 3mm 5mm
	}
}

@media screen and (min-width: 1250px) {
	body {
		overflow-x: hidden;
		scrollbar-width: none;
		-ms-overflow-style: none
	}

	.c-menuLv1 a {
		font-size: calc(10pt + .32vw) !important;
		padding: 6px .5em
	}

	.l-footerLinks>li a {
		font-size: calc(9pt + .28vw) !important
	}

	.t1,
	.t2 {
		font-size: 1.5vw !important
	}

	.o-mainCardDesc {
		font-size: 1.3vw
	}

	.c-mainCard__desc {
		padding: .8vw 1.5vw
	}
}

@media screen and (min-width: 1600pt) {
	body {
		overflow-x: hidden;
		scrollbar-width: none;
		-ms-overflow-style: none
	}

	.t1,
	.t2 {
		font-size: 30px !important
	}

	.l-wrap {
		max-width: 1600pt
	}

	.l-content__main {
		max-width: calc(1600pt - 28mm)
	}
}

@media screen and (min-width: 1680pt) {
	.l-content__side:hover {
		left: calc(40vw - 1030pt)
	}
}

}
