css:
@-moz-document domain("420chan.org") {
html,
body {
    background: url('http://i.imgur.com/QUi2obe.jpg') #121212 bottom right no-repeat fixed !important;
    /*background:#000 !important;*/
    
    color: #FFF !important;
}
a,
a img,
span.plus {
    color: #4ada6f !important
}
body,
.replymode,
.border,
.hborder,
.postarea {
    margin: 0 !important;
    padding: 0 !important;
    width: 100% !important;
}
#topzone,
#midzone,
#footer,
.replymode,
.postblock,
input:not([type="submit"]):not([type="reset"]),
select,
textarea,
th[bgcolor="#e04000"] .content,
.reply,
#watchedthreads,
.nigratrain {
    background-color: #000 !important;
}
}
