css:
@-moz-document domain("ruten.com.tw") {

#AdFocusContainer,
.site_evt,
.carousel,
.block_E,
.rt-footer,
.ad-container,
.isImpress,
.side-shadow,
.sidebar-ad-section {
	display: none !important;
}

/* Global */
body {
	background-color: #f3f2ec;
}

a:hover {
	color: #137;
	text-shadow: 0 0 1px #aaa;
	text-decoration: none;
}

.rt-page {
	width: calc(100vw - 6mm);
	min-width: 1200px;
	max-width: 1500px;
	padding: 55px 0 3mm;
	margin: 0 auto;
}

.rt-header {
	padding: 0 3mm;
}

.index-header {
	min-width: 0;
}

.index-header-top {
	background-color: #eee;
}

.rt-header,
.rt-footer {
	box-shadow: 0 0 1mm 1px #888;
	margin: 0;
}

.index-page .rt-wrap,
.rt-store-main {
	width: auto;
	min-width: 1100px;
	max-width: none;
	margin: 0 auto;
	padding: 0
}

.rt-wrap {
	padding: 0;
}

.rt-goods {
	width: auto
}

.main-container {
	width: calc(100% - 200px);
}

.rt-page>.breadcrumb {
	margin: 0 0 1mm;
	line-height: 1.4em;
}

.rt-page>.breadcrumb .breadcrumb-text {
	padding: 4px 3mm 4px 4px !important;
	border-radius: 5px 4mm 4mm 5px;
	background-color: #f8f8f8;
	box-shadow: inset 0 0 3px 1px #888;
}

.breadcrumb-text a {
	display: inline-block;
	padding: 0 6px;
	border-radius: 1em;
}

.breadcrumb-text a:first-child {
	padding-left: 3px;
	border-radius: 1mm 1em 1em 1mm;
}

.breadcrumb-text a:hover {
	text-decoration: none;
	color: #630;
	box-shadow: 0 0 0 1px #630;
}

.everyone-is-looking-for-page>.everyone-is-looking-for-item {
	float: left;
}

/* in store */
.custom-store-banner,
.rt-store-cstore-ad-slot {
	display: none;
	margin: 2mm auto;
	max-height: 6mm;
	opacity: .4;
	overflow: hidden;
	transition: all .4s .3s;
}

.custom-store-banner:hover,
.rt-store-cstore-ad-slot:hover {
	max-height: 300vh;
	opacity: 1;
	overflow: scroll;
	transition: all .3s .2s;
}

.rt-store-main {
	overflow: visible;
}

.sidebar-section {
	border: 2mm;
	overflow: hidden;
	border-radius: 1.5mm;
	box-shadow: 0 0 1.5mm #888;
}

.rt-store-sidebar .category-listing li a {
	padding: 4px 10px;
	margin: 1px 0;
	line-height: 1.5em;
	border-radius: 2mm;
	transition: .2s;
}

.rt-store-sidebar .category-listing li>a:hover {
	color: #338;
	background-color: rgba(228, 250, 228, .5);
	box-shadow: 1px 1px 4px 1px #777;
	transition: 0s;
}

.rt-store-sidebar .category-listing .category-current {
	font-weight: 400;
	color: #333;
	background-color: rgba(128, 128, 128, .2);
	box-shadow: inset 1px 1px 4px #777;
}

.class-title {
	padding: 3mm 0 0;
}

.rt-store-sidebar {
	padding: 0 1mm 0 0;
}

/* item list */
dd[_oversea] {
	opacity: .3;
	transition: 1s;
}

dd[_oversea]:hover {
	opacity: 1;
}

.block_C {
	width: calc(100% - 212px);
}

.search_form .s_shining {
	background: none;
	box-shadow: inset 0 0 0 1px #a00;
	animation: none;
}

.ui-search .s_grid .site_loop dd {
	background-color: #e6e6e8;
	border: none;
	box-shadow: 0 0 0 1px #aaa;
}

.ui-search .s_grid .site_loop dd:hover {
	z-index: 2;
	background-color: #f5f5f5;
	box-shadow: 0 0 2mm 1px #aaa;
}

.rt-store-goods-listing {
	width: auto;
}

.rt-store-goods-disp-mix {
	padding: 1mm;
	margin: 0 1mm 1mm 0;
	background-color: #ccc;
	border-radius: 2mm;
	border-top: none;
	transition: background-color .3s;
}

.rt-store-goods-disp-mix:hover {
	background-color: #ededf6;
	transition: background-color 0s;
}

.s_grid .prod_img_wrap {
	margin: 0 auto;
}

.prod_img_wrap,
.s_grid .prod_img,
.s_grid .prod_noimg {
	width: 100% !important;
	height: 180px;
}

.s_grid .prod_img img,
.rt-store-goods-disp-mix .item-img img {
	width: 90% !important;
	max-width: none;
	object-fit: cover;
}

.keywords {
	margin: 1mm auto;
	border: none;
	border-radius: 3mm;
	background-color: #eee;
	box-shadow: inset 0 0 2mm #888;
}

.s_list .col3f {
	padding: 2mm 0;
}

.ui-search .s_list .c2f {
	width: calc(100% - 400px);
}

/* item info */
.main-content {
	padding: 1mm 3mm;
	background-color: #fff;
	border-radius: 3mm;
	box-shadow: 0 1mm 4mm #aaa;
}

.rt-flex-grid-4-5 {
	flex: 1200px;
}

.intro-section-right {
	flex: 0 0 900px;
}

.rt-tab-wrap {
	width: 1200px;
}

.mybid-default-page .column-base h3 {
	font-size: 13pt;
}

.item-info-detail {
	max-width: calc(100% - 220px);
}

.item-detail-table .title {
	width: 5em;
}

.item-detail-inline {
	margin-left: calc(5em + 5px);
}

.rt-pagination li a {
	font-size: 11pt;
}

.rt-button-mini {
	min-height: 1em;
	line-height: 2;
	font-size: 11pt;
}

iframe#embedded_goods_comments {
	width: 100%;
}

.rt-ad-product-list .rt-ad-heading-wrapper {
	max-width: 950px;
	margin: 0 auto;
}

.rt-ad-product-list .rt-ad-list-container {
	margin: 0 auto;
	border-bottom: 1px solid #444;
}

}
