css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.netvibes.com") {

body {font-family: Tahoma, "Segoe UI", Verdana !important; font-size: 120% !important;}

#footer {display: none !important;}
#top {display: none !important;}
#topLinks {visibility: hidden !important; height: 3px !important;}
#aNewTab {display: none !important;}
#savingStatus {display: none !important;}
.gripper {display: none !important;}
#divTabs .unread {display: none !important;}

#header {display: none !important;}

.actions {display: none !important;}
.showHide {visibility: hidden !important; width: 0 !important;}
.ico {display: block !important;}
.options {display: none !important;}
.moduleHeader {cursor: default !important;}
.actions-tab-options {display: none !important;}

/* twitter widget */
.twitter > div:first-child {display: none !important;}
.twitter a.time {display: none !important; color: #ccc !important;}
.twitter a small {display: none !important;}
/* Notes */
.postItEdit {border: none !important; margin-top: 1px !important;}
/* flickr */
.flickr > div > div {padding: 2px !important; /*border: 2px solid #000 !important;*/}

}
