@-moz-document domain(feedly.com) {
  html {
    background-color: #002B36;
}
html{    background-color: #002B36;
}

* {
    color: #93a1a1;
    border-color: rgba(0, 43, 54, 0.8) !important;
	background-color: rgba(0, 43, 54, 0.8)!important;
}


svg{
color:#fff;
}

#feedlyTitleBar {
    color: #002B36;
}
/* Hide Single View Close Tab */


.floatingEntryContent-tab {
    display: none;
}

/* Links */
.headerInfo-expanded-scroller section,
a,
.action {
    color: #859900 !important;
    border: none !important;
}


/* Inputs */

input {
    color: #93a1a1 !important;
    border: none;
}


.u0Summary {
    color: #93a1a1;
}
/* Profile */

#fixedProfile {
    bottom: -35px;
    background-color: #001F27;
}
#fixedProfile:hover {
    bottom: 0px;
}
#feedlyTabs {
    height: 100% !important;
}
button,
.slideBumper div,
.hoverableIcon,
.headerInfo-expanded-img,
.pageAction,
.icon{
 	background-color: rgba(255, 255, 255, 0.2)!important;
}
}
