css:
@-moz-document domain("chromestatus.com"), domain("chromedevtools.github.io") {
:root {
    -webkit-filter: invert(0.9);
    -moz-filter: invert(0.9);
    filter: invert(0.9);
    background-color: #1a1a1a;
}
chromedash-legend[opened]:after {
    background-color: #eee;
    opacity: 0.9;
}
chromedash-featurelist {
    max-width: none;
}
}
