css:
@-moz-document domain("cpplover.blogspot.com") {
body > div {
  width: 800px;
  margin: 0 auto;
}

.fauxcolumns {
  display: flex;
  flex-direction: column;
}

.main-inner div,
.main-inner .columns {
  width: auto;
  float: none;
  margin: 0;
  padding: 0;
}

.main-inner .column-center-inner .section {
  margin: 0 40px;
}

h3.post-title, .comments h4 {
  font-size: 28px;
}

.post-body {
  line-height: 1.6;
}

body {
  background-image: none;
  background-color: #333;
}

.body-fauxcolumns,
.body-fauxcolumn-outer .cap-top {
  display: none;
}

.content-outer {
  box-shadow: none;
}

.post-body h1 {
  margin-top: 1em;
  line-height: 1.2;
}

.main-inner .column-right-outer {
  width: auto;
  margin: 0;
}

}
