css:
/*--------------------------------------------------------------------------------
  Stylish - Icons only when sidebar mode
  --------------------------------------------------------------------------------
  Author: Louis H - Tested on: Windows XP Firefox 3.0.4 - Last Update: 2.1.2009
  --------------------------------------------------------------------------------
  Description:
    Show icons only in sidebar mode so that the scrollbar can be display without enlarge the sidebar width
  --------------------------------------------------------------------------------*/


@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url("chrome://stylish/content/manage.xul?sidebar=1") {
#toolbar toolbarbutton .toolbarbutton-text {
display:none !important;
}

#stylish {
margin: -8px -15px -10px -10px !important;  
}

#toolbar {
padding-left: 5px !important;
}

}
