css:
@-moz-document domain("www.nba.com"), domain("stats.nba.com") {
#content, .boxscore__table--row {
	border: none
}
#block-homepagefooter.block-homepagefooter, #nba_tos, .article__detail--sidebar .nba-ad, .block-globalheaderadblock, .footer, .nba-ad-container.stretch, .scoreboard .nba-ad-container, .video__detail--sidebar .nba-ad {
	display: none
}
header.nba-nav-header {
	margin: 0
}
#main_sidebar {
	height: 100%;
	margin: 0
}
.scoreboard .nba_bottom_scoreboard--wrapper {
	height: calc(100vh - 155px)
}
@media screen and (max-width:45.9375em) {
	#content {
		height: 100%
	}
}
@media (max-width:736px) {
	#main {
		padding-top: 50px
	}
}
.article__detail--sidebar, .video__detail--sidebar, .team__list_scroll {
	max-height: calc(100vh - 70px)
}
#player-list.static .players-wrapper {
    	max-height: calc(100vh - 150px);
}

#player-list.active .players-wrapper  {
    max-height: calc(100vh - 115px);
}

#content {
    margin: 0;
}

.nba-secondary-nav__container {
    height: 100%;
    padding: 20px 0 10px 25px;
    width: 357px;
}
}
