css:
@-moz-document domain("userstyles.org") {
#main-header {
	font-family: Times roman numeral;
	background-color: green;
	padding: 5px;
	margin-bottom: 10px;
	margin-left: 10px;
	border-radius: 10px;
}
.HP,
.HP:hover,
.HP:active {
	color: blue;
}
#left-sidebar>* {
	background-color: grey;
	border-radius: 5px;
	margin: 5px;
	padding: 5px;
}
body {
	font-family: Times roman numeral;
	font-size: medium;
	max-width: 980px !important;
	margin-left: auto !important;
	margin-right: auto !important;
}
a:visited {
	color: black;
}
a {
	color: blue;
}
}
