css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.ifolder.ru/1"),
url-prefix("http://www.ifolder.ru/2"),
url-prefix("http://www.ifolder.ru/3"),
url-prefix("http://www.ifolder.ru/4"),
url-prefix("http://www.ifolder.ru/5"),
url-prefix("http://www.ifolder.ru/6"),
url-prefix("http://www.ifolder.ru/7"),
url-prefix("http://www.ifolder.ru/8"),
url-prefix("http://www.ifolder.ru/9"),
url-prefix("http://ifolder.ru/1"),
url-prefix("http://ifolder.ru/2"),
url-prefix("http://ifolder.ru/3"),
url-prefix("http://ifolder.ru/4"),
url-prefix("http://ifolder.ru/5"),
url-prefix("http://ifolder.ru/6"),
url-prefix("http://ifolder.ru/7"),
url-prefix("http://ifolder.ru/8"),
url-prefix("http://ifolder.ru/9") {

#create-own-ifolder,
.text-center,
.google_ads,
#left-col,
#right-block,
#content-block .dashed div:first-child,
.begun,
#footer,
body > a,
#torrent_download {
  display: none !important;
}

.dashed {
  border: 0 !important;
  margin: 0 !important;
  padding: 0 !important;
}

.download-direct-link {
  overflow: auto !important;
}

}

@-moz-document url-prefix("http://ints.ifolder.ru/ints/") {
#Table3 tbody tr:first-child,
#Table3 tbody tr:last-child,
body > table > tbody > tr:first-child > td table {
  display: none !important;
}
}

@-moz-document url-prefix("http://ints.ifolder.ru/ints/frame/") {
.main_table > tbody > tr:first-child,
.main_table > tbody > tr:last-child,
.main_table > tbody > tr > td[colspan="3"],
.side_ban,
ins,
.main_table > tbody > tr > td > div,
caption,
#Table1 > tbody tr:last-child {
  display: none !important;
}
}
