css:
@-moz-document domain("dontbreakthechain.com") {
body {
    background-color: #3A3A3A;
    color: #eee;
}

h1, h2, h3, h4, h5, h6 {
    color: #eee;
}   

a {
    color: #166DC9;
}

.month a.day {
    color: #eee;
}

.month .today a {
    background-color: rgba(255,255,255,.25);
}

ul#chains li.current-chain {
    background: none;
    padding-left: 30px;
}

}
