css:
@-moz-document domain('stash.ai'), url-prefix('https://stash.ai/stash'), url-prefix('https://stash.ai/inbox'), url-prefix('https://stash.ai/reminders') {
  body {
      background-color: #333;
	  font-size: 20px;
  }

  .item .title-container .title {
      color: white;
  }

  .item:hover,
  .item.selected {
      background-color: #111 !important;
  }

  .left-sidebar {
      background-color: #111;
  }

  .left-sidebar .sidebar-top .location.selected {
      background-color: #333;
  }

  .item .aux {
      color: white;
  }

  .browse-circle-container .browse-circle-number,
  .browse-circle-container .browse-circle-title {
      color: white;
  }

  .browse-circle-container.fake .browse-circle-number,
  .browse-circle-container.fake .browse-circle-title {
      color: #333;
  }

  .item .tag {
      background-color: #111;
      color: white;
  }

  .filter-circles .more {
      background-color: #111;
  }

  .filter-circles .more .down-indicator:after {
      background-color: #111;
  }
}
