css:
@-moz-document domain("recolor.me") {
body.plains_dark #global_container {
	background:  "(0,0,0,0)";
}
#global_container {
	position: relative;
	z-index: 1;
	width: 100%;
	max-width: 100%;
	background:no-repeat center top transparent;
	margin: 0 auto;
	padding: 125px 0 0;
	min-height: 300px;
	text-align: center;
}
body.plains_dark {
	background: repeat scroll center 520px #5e4f48;
}
#global_container h2 {
	position: absolute;
	z-index: 2;
	top: 50px;
	left: 0px;
	color: lightblue;
	text-align: left;
	font-size: 20px;
	text-shadow: 0 1px 2px #000;
}
#login_extras {
	position: absolute;
	z-index: 0;
	width: 0%;
	height: 0px;
	left: 0;
	top: 0;
	text-align: center;
	overflow: hidden;
	white-space: nowrap;
}
#login_form p {
	text-align: center;
	padding: 7px 0 12px;
	padding: 8px 0;
	color: #fff;
	text-shadow: 0 0 0px #000;
}
#login_status {
	min-height: 0px;
}
#login_create a {
	font-size: 25px;
}
}
