css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.goonfleet.com"), url-prefix("http://goonfleet.com") {

/* Get rid of the violent violet */
body {
  background: gray ! important;
  background-image: none;
}

.page {
  background: none ! important;
}

.tcat {
  background: #555555 ! important;
  background-image: none;
}

.alt2 {
  background: #eeeeee ! important;
}

.thead {
  background-color: gray ! important;
}

/* Remove blank space between posts */
.page > div:first-child > div:first-child {
  padding: 0px ! important;
}

/* Remove the top bar because it takes up space; we lose Reply, but it shows up at the bottom */
a#poststop + table {
  display: none ! important
}

/* you are not a special snowflake */
.smallfont, .smallfont * {
  font-size: 10px ! important;
}

}
