css:
@-moz-document domain("lichess.org") {

/* Purple movement markers */

/* color picker to get rgba color codes http://www.hexcolortool.com */

 .cg-board square.selected {background-color: rgba(15,0,125,0.5) !important;} /* selected piece */

 .cg-board square.move-dest {background: radial-gradient(rgba(15,0,125,0.3) 17%,#208530 0,rgba(0,0,0,0.3) 0,rgba(0,0,0,0) 0) !important;}  /* destination dots, empty spaces */

 .cg-board square.move-dest:hover {background: rgba(15,0,125,0.3) !important;} /* highlight square on hover */
    
 .cg-board square.oc.move-dest {background: radial-gradient(transparent 0%,transparent 60%,rgba(15,0,125,0.3) 80%) !important;} /* available capture */

  

