css:
@-moz-document domain("play.google.com") {
 
  .body-content[itemtype="http://schema.org/MobileApplication"]  
  .inner-container .main-content {width: 80vw !important; display: block !important;}

  .body-content[itemtype="http://schema.org/MobileApplication"]  
  .inner-container .main-content .info-container .info-box-bottom {width: calc(100% - 240px) !important;}
  
  .body-content[itemtype="http://schema.org/MobileApplication"]
  .inner-container .main-content .details-section-contents {max-width: inherit !important;}
  
  .body-content[itemtype="http://schema.org/MobileApplication"]
  .inner-container .secondary-content {width: 80vw !important; display: block !important; margin: 0px !important;}
  .body-content[itemtype="http://schema.org/MobileApplication"]
  .inner-container .secondary-content .details-section .heading {max-width: inherit !important;}
  .body-content[itemtype="http://schema.org/MobileApplication"]
  .inner-container .secondary-content .details-section .cards {max-height: inherit !important;}
  
  .body-content[itemtype="http://schema.org/MobileApplication"]
  .inner-container .secondary-content .card.medium-minus {height: 320px !important; width: 170px !important; margin-right: 7px !important;}
  
  .body-content[itemtype="http://schema.org/MobileApplication"]
  .inner-container .secondary-content .card.medium-minus .cover {float: inherit !important; width: 170px !important;}
  .body-content[itemtype="http://schema.org/MobileApplication"]
  .inner-container .secondary-content .card.medium-minus .details {margin-left: 0px !important;}
  .body-content[itemtype="http://schema.org/MobileApplication"]
  .inner-container .secondary-content .card.medium-minus .reason-set {margin-left: 0px !important;}
  
}
