css:
@-moz-document domain("www.unknowncheats.me") {
.page,
body,
.tborder,
.alt1,
.alt2,
.info
{
    background-color: #2a2b2a !important;
}

textarea
{
    background-color: #2a2b2a !important;
    color: #fff !important;
}

.alt1,
.alt2,
.time,
font[color="black"]
{
    color: #CCC !important;
}
td,
.info,
.alt1,
textarea
{
    background: #414440 !important;
    border-top-width: 0px !important;
    border-left-width: 0px !important;
    border-right-width: 0px !important;
    border-bottom-width: 0px !important;
}

.table
{
    border: 0px !important;
    background-color: #2a2b2a !important;
}

a
{
    color: #CCC !important;
}

a:visited
{
    color: #2a2b2a !important;
}

.thead,
.smallfont,
.tcat,
.navbar
{
    color: #CCC;
}
/* removes user title */
center div font
{
    display: none !important;
}

/*removes "shoutbox is not for support"*/
#dbtech_vbshout_titlemenu1
{
 display: none !important;   
}

/* Removes UC title */
/*#Table1 > tbody > tr > td.alt1
{
    display: none !important;
}*/

body > div > table.border > tbody > tr > td > table:nth-child(1) *
{
    display: none !important;
}


img[src="ambience/misc/expand_main_table.gif"],
img[src="ambience/misc/ls_main_table_top.gif"],
img[src="ambience/misc/rs_main_table_top.gif"],
img[src="ambience/misc/menu_open.gif"],
img[src="//www.unknowncheats.me/forum/ambience/misc/expand_main_table.gif"],
img[src="//www.unknowncheats.me/forum/ambience/misc/ls_main_table_top.gif"],
img[src="//www.unknowncheats.me/forum/ambience/misc/rs_main_table_top.gif"]
{
    display: none !important;
}

}
