@namespace html url(http://www.w3.org/1999/xhtml);
@namespace xul url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document
    regexp("https?://(?!.*(facebook\.com|wykop\.pl|google\.com|allegro\.pl|olx\.pl|aliexpress\.com|youtube\.com|stackoverflow\.com|dropbox\.com|bank\.pl|wikipedia\.org)).*/.*")
    , url-prefix("ftp://")
    , url-prefix("file://")
    , url-prefix("about:")
    , url-prefix("javascript")
    , url(about:blank), url-prefix("about:neterror")
{

*::-moz-selection
   {
      background-color: #363 !important;
      color: #222 !important;
   }

div:not(:empty)
   {
      background-color: #181818 !important;
      color: #aaa !important;
   }
div:empty
   {
      border:1px solid #121212;
   }
html
   , body
   {
      background-image: url("data:image/gif;base64,R0lGODlhBAAEAKECABERESQkJP///////yH5BAEKAAIALAAAAAAEAAQAAAIFhB6nhlIAOw==") !important;
      background-repeat: repeat !important;
      background-color: #181818 !important;
      color: #aaa !important;
   }

a
   , a:link
   , a:visited
   , a:hover
   , a:active
   {
      color:inherit;
      text-decoration: none;
   }
    
a:hover
   , a:active
   {
      text-decoration: underline;
   }
    
input
   , textarea
   , select
   , option
   , button
   {
      -webkit-filter: none !important;
      background-color: #232323 !important;
      color: #aaa !important;
      border-color: orange !important;
      border-style: solid;
      border-width: 1px;
   }

button
   , input[type="button"]
   , *:empty
   , a
   , a:link
   , a:visited
   , img
   {
      -webkit-filter: grayscale(1) !important;
   }

button:hover
   , img:hover
   , body > * > img:hover
   , body > img:hover
   
   , body > div:hover
   , body > * > div:hover
   
   , body > * > p:hover
   , body > p:hover
   
   , * a:hover
   , * a:active
   
   , * button:hover
   , * input[type="button"]:hover
   
   , *:empty:hover
   {
      -webkit-filter: none !important;
   }

}

/* GLOBAL SETTS */
input
   , select
   , option
   , textarea
   , button
   {
      background-color: #131313 !important;
      color: #eaeaea !important;
      border: 1px solid #222 !important;
      padding:2px 4px;
   }
input:focus
   , select:focus
   , option:focus
   , textarea:focus
   , button:focus
   {
      border-color: orange !important;
   }
