css:
@-moz-document url-prefix("http://www.ida.liu.se/opendsa/") {
.section > p {
	font-family: "Liberation Serif";
    font-size: 27px;
}

.topic > p {
	font-size: 18px;
}

div.content {
	margin-left: 400px;
    margin-right: 400px;
}

.section > li, .section > ol > li {
	font-size: 20px;   
}

.section > h2 {
    font-size: 30px;
}

.section > h1 {
    font-size: 34px;
}

blockquote > div {
	font-size: 20px !important;
}

div#ProblemAlgorithmCON > p {
	font-size: 16px;
}

.mod_complete {
	position: fixed;
    color: #368d5c !important;
}

div.bottomnav > p, div.topnav > p, .email_div > a {
	line-height: normal;
	font-size: 16px;
    color: #4f6cc1 !important;
}

#nextmod, #nextmod1 {
	color: #368d5c;
}

#prevmod, #prevmod1 {
	color: #8d363b;
}

#username-link {
	position: fixed;
	font-size: 20px;
	color: #4f6cc1 !important;
}

.topic > .math > .MathJax, .topic > .math > .MathJax_Display {
	font-size: 22px;
}

.section-headline {
	text-align: center;
}

#points-area {
	display: block;
}
}
