css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.jetbrains.com/help/") {
  
  /* MAIN DOCUMENT */
  main.panel._main, main.panel._main h1, main.panel._main h2, main.panel._main h3, main.panel._main h4, main.panel._main h5, .article p, main.panel._main ul, main.panel._main li {
    background-color: #2D2D30;
    color:#E0DDF0;
  }
  
  .panel._main .panel__header, .panel._main .panel__header h1, .panel._main .panel__header h2, .panel._main .panel__header h3 {
    background-color: #1C1C1C;
    color:#E0DDF0;
  }
  
  button.feedback__button._question {
    color:#E0DDF0;
  }
  
  .tip, .note, .note p:last-child, .tip p:last-child, .todo p:last-child, .warning p:last-child {
    background-color: #252526;
  }
  
  .code {
    background-color: #1C1C1C;
  }
  
  .code-block__wrapper  {
    background-color: #252526;
    color: #0887d7;
    border-top-color: #1C1C1C;
  }
  
  .code-block .n, .code-block .o, .code-block .p {
    color: #E0DDF0;
  }
  
   .code-block .nt, .code-block .na, .code-block .k, .code-block .kc, .code-block .kd, .code-block .kn, .code-block .kr, .code-block .kt {
    color: #0887d7;
  }
  
  .kbd, .shortcut, kbd {
    background-color: #252526;
    color: #E0DDF0;
    box-shadow: 0 2px 0 -1px #1C1C1C,0 2px 0 #252526;
  }
  

  
  .article table th, .article table td {
    color:#E0DDF0;
  }
  
  /* LEFT MENU */
  .panel__header {
    background-color: #252526;
  }
  
  .panel__header .search-box__input {
    color: #3F3F46;
  }
  
  .container._nav, ul.nav-tree__list, nav.panel__content {
    background-color: #252526;
  }
  
  a.nav-tree__link {
    color: #A8A5B9;
  }
  
  
}
