css:
@-moz-document regexp("(?=.*?:8989).*") {
.started #page{background-color:transparent;box-shadow:0 0 10px 1px transparent;color:#fff}
.table{background-color:#111;box-shadow:0 0 10px 1px #000;color:#fff}
.btn-default,.approval-status-cell .popover{background-color:#111;color:#fff}
.btn-default:hover,.btn-default:focus,.btn-default.focus,.popover-title{background-color:#333;color:#fff}
.thingy,.metadata-item{background-color:#444;box-shadow:0 0 10px 1px #444;color:#fff;margin:10px}
legend,.event .date h4,.series-item a{color:#ccc}
.nav-tabs>li.active>a,.nav-tabs>li.active>a:hover,.nav-tabs>li.active>a:focus{background-color:#555;color:#fff}
.form-control,.bootstrap-tagsinput{background-color:#555!important;color:#fff!important}
th.sortable:hover{background:#353535}
.series-season,.add-thingy, .series-posters .series-posters-item{background-color:#111;box-shadow:0 0 10px 1px #000;color:#fff;margin:10px;opacity:.96}
a,.episode-title-cell,.event h4,.event .episode-title{color:#36c6f4}
.table-hover>tbody>tr:hover{background-color:#353535}
a:hover,a:focus,.episode-title-cell:focus,.episode-title-cell:hover,.event .episode-title:hover,.event .episode-title:focus{color:#7fe0ff;text-decoration:underline}
.nav>li>a:hover,.nav>li>a:focus{background-color:#444;text-decoration:none}
.calendar th{background-color:#111}
.calendar .fc-state-highlight,.add-card .center{background:#444}
.label-info{background-color:#0c2b35}
.label-success{background-color:#2e582e}
.label-danger{background-color:#7b2e2b}
.label-warning{background-color:#735730}
.modal-content{background-color:#222}
button,html input[type="button"],input[type="reset"],input[type="submit"]{background-color:#111;color:#ccc}
.btn:hover,.btn:focus,.btn.focus{color:#fff}
.alert-warning{background-color:#111;border-color:#111;color:#ccc}
}
